  const TTS_CACHE_TTL = 1000 * 60 * 60; // 1 hour

  app.post("/api/experiences/:experienceId/lessons/:lessonId/tts", authenticateWhop, requireExperienceAccess, async (req: AuthenticatedRequest, res) => {
    try {
