shared/whop-types.ts.content:
export type LessonType = "text" | "video" | "pdf" | "multi" | "quiz" | "knowledge_check";
export type EmbedType = "youtube" | "loom";
export type Visibility = "visible" | "hidden";
export type QuestionType = "short_answer" | "true_false" | "multiple_choice" | "multiple_select";
export type VideoStatus = "uploading" | "created" | "ready";

export interface WhopAttachment {
  id: string;
  filename: string | null;
  content_type: string | null;
  url: string | null;
}

export interface WhopVideoAsset {
  id: string;
  asset_id: string | null;
  playback_id: string | null;
  signed_playback_id: string | null;
  status: VideoStatus;
  audio_only: boolean;
  duration_seconds: number | null;
  signed_video_playback_token: string | null;
  signed_thumbnail_playback_token: string | null;
  signed_storyboard_playback_token: string | null;
  created_at: string;
  updated_at: string;
  finished_uploading_at: string | null;
}

export interface WhopAssessmentOption {
  id: string;
  option_text: string;
  is_correct: boolean;
  order: number;
}

export interface WhopAssessmentQuestion {
  id: string;
  question_text: string;
  question_type: QuestionType;
  correct_answer: string | null;
  order: number;
  created_at: string;
  image: WhopAttachment | null;
  options: WhopAssessmentOption[];
}

export interface WhopLessonSummary {
  id: string;
  title: string;
  order: number;
  lesson_type: LessonType;
  visibility: Visibility;
}

export interface WhopLesson {
  id: string;
  title: string;
  order: number;
  lesson_type: LessonType;
  visibility: Visibility;
  content: string | null;
