import { MongoClient, Db, Collection, ObjectId } from "mongodb";

const uri = process.env.MONGODB_URI;

if (!uri) {
  console.warn("MONGODB_URI not set - MongoDB features will be disabled");
}

let client: MongoClient | null = null;
let db: Db | null = null;

export async function connectMongoDB(): Promise<Db | null> {
  if (!uri) {
    return null;
  }

  if (db) {
    return db;
  }

  try {
    client = new MongoClient(uri);
    await client.connect();
    db = client.db();
    console.log("Connected to MongoDB Atlas");
    return db;
  } catch (error) {
    console.error("Failed to connect to MongoDB:", error);
    return null;
  }
}

export async function getDB(): Promise<Db | null> {
  if (db) return db;
  return connectMongoDB();
}

export async function closeMongoDB(): Promise<void> {
  if (client) {
    await client.close();
    client = null;
    db = null;
  }
}

export interface UserPreferences {
  _id?: ObjectId;
  whopUserId: string;
  theme: "light" | "dark";
  lastVisitedCourse: string | null;
  lastVisitedLesson: string | null;
  fontSize: "small" | "medium" | "large";
  createdAt: Date;
  updatedAt: Date;
}

export interface AIGenerationLog {
  _id?: ObjectId;
  userId: string;
  prompt: string;
  generatedCourse: any;
  whopCourseId: string | null;
  status: "generated" | "saved" | "discarded";
  createdAt: Date;
}

export interface CourseAnalytics {
  _id?: ObjectId;
  whopCourseId: string;
  views: number;
  uniqueVisitors: string[];
  avgTimeSpent: number;
  lastViewedAt: Date;
  dailyViews: { date: Date; count: number }[];
}

export interface LocalUserData {
  _id?: ObjectId;
  whopUserId: string;
  email: string | null;
  username: string | null;
  profilePicUrl: string | null;
  role: "creator" | "member";
  whopCompanyId: string | null;
  createdAt: Date;
  updatedAt: Date;
}

export class MongoStorage {
  private async getUserPreferencesCollection(): Promise<Collection<UserPreferences> | null> {
    const database = await getDB();
    return database?.collection<UserPreferences>("userPreferences") || null;
  }

  private async getAIGenerationLogsCollection(): Promise<Collection<AIGenerationLog> | null> {
    const database = await getDB();
    return database?.collection<AIGenerationLog>("aiGenerationLogs") || null;
  }

  private async getCourseAnalyticsCollection(): Promise<Collection<CourseAnalytics> | null> {
    const database = await getDB();
    return database?.collection<CourseAnalytics>("courseAnalytics") || null;
  }

  private async getLocalUserDataCollection(): Promise<Collection<LocalUserData> | null> {
    const database = await getDB();
    return database?.collection<LocalUserData>("localUserData") || null;
  }

  async getUserPreferences(whopUserId: string): Promise<UserPreferences | null> {
    const collection = await this.getUserPreferencesCollection();
    if (!collection) return null;
    return collection.findOne({ whopUserId });
  }

  async updateUserPreferences(whopUserId: string, prefs: Partial<UserPreferences>): Promise<UserPreferences | null> {
    const collection = await this.getUserPreferencesCollection();
    if (!collection) return null;

    const now = new Date();
    const result = await collection.findOneAndUpdate(
      { whopUserId },
      {
        $set: { ...prefs, updatedAt: now },
        $setOnInsert: { whopUserId, createdAt: now },
      },
      { upsert: true, returnDocument: "after" }
    );
    return result;
  }

  async logCourseGeneration(log: Omit<AIGenerationLog, "_id" | "createdAt">): Promise<AIGenerationLog | null> {
    const collection = await this.getAIGenerationLogsCollection();
    if (!collection) return null;

    const doc: AIGenerationLog = {
      ...log,
      createdAt: new Date(),
    };
    const result = await collection.insertOne(doc);
    return { ...doc, _id: result.insertedId };
  }

  async updateGenerationLog(logId: string, updates: Partial<AIGenerationLog>): Promise<void> {
    const collection = await this.getAIGenerationLogsCollection();
    if (!collection) return;

    await collection.updateOne(
      { _id: new ObjectId(logId) },
      { $set: updates }
    );
  }

  async getGenerationHistory(userId: string): Promise<AIGenerationLog[]> {
    const collection = await this.getAIGenerationLogsCollection();
    if (!collection) return [];

    return collection.find({ userId }).sort({ createdAt: -1 }).limit(50).toArray();
  }

  async trackCourseView(whopCourseId: string, userId: string): Promise<void> {
    const collection = await this.getCourseAnalyticsCollection();
    if (!collection) return;

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    await collection.updateOne(
      { whopCourseId },
      {
        $inc: { views: 1 },
        $addToSet: { uniqueVisitors: userId },
        $set: { lastViewedAt: new Date() },
        $push: {
          dailyViews: {
            $each: [{ date: today, count: 1 }],
            $slice: -90,
          },
        },
      },
      { upsert: true }
    );
  }

  async getCourseAnalytics(whopCourseId: string): Promise<CourseAnalytics | null> {
    const collection = await this.getCourseAnalyticsCollection();
    if (!collection) return null;

    return collection.findOne({ whopCourseId });
  }

  async getOrCreateLocalUser(data: Omit<LocalUserData, "_id" | "createdAt" | "updatedAt">): Promise<LocalUserData | null> {
    const collection = await this.getLocalUserDataCollection();
    if (!collection) return null;

    const now = new Date();
    const result = await collection.findOneAndUpdate(
      { whopUserId: data.whopUserId },
      {
        $set: { ...data, updatedAt: now },
        $setOnInsert: { createdAt: now },
      },
      { upsert: true, returnDocument: "after" }
    );
    return result;
  }

  async updateLocalUser(whopUserId: string, updates: Partial<LocalUserData>): Promise<LocalUserData | null> {
    const collection = await this.getLocalUserDataCollection();
    if (!collection) return null;

    const result = await collection.findOneAndUpdate(
      { whopUserId },
      { $set: { ...updates, updatedAt: new Date() } },
      { returnDocument: "after" }
    );
    return result;
  }

  async getLocalUser(whopUserId: string): Promise<LocalUserData | null> {
    const collection = await this.getLocalUserDataCollection();
    if (!collection) return null;

    return collection.findOne({ whopUserId });
  }
}

export const mongoStorage = new MongoStorage();

