import { whop } from "./whop";
import type {
  WhopCourse,
  WhopCourseWithChapters,
  WhopChapter,
  WhopChapterWithLessons,
  WhopLesson,
  WhopStudent,
  WhopStudentFull,
  WhopInteraction,
  WhopInteractionFull,
  CreateCourseInput,
  UpdateCourseInput,
  CreateChapterInput,
  UpdateChapterInput,
  CreateLessonInput,
  UpdateLessonInput,
  WhopPaginatedResponse,
  CourseListItem,
} from "@shared/whop-types";

export class WhopCoursesService {
  async listCourses(experienceId?: string): Promise<WhopCourse[]> {
    try {
      const courses: WhopCourse[] = [];
      const params: any = { first: 100 };
      if (experienceId) {
        params.experience_id = experienceId;
      }

      for await (const course of whop.courses.list(params)) {
        courses.push(course as unknown as WhopCourse);
      }
      return courses;
    } catch (error) {
      console.error("Failed to list courses:", error);
      return [];
    }
  }

  async getCourse(courseId: string): Promise<WhopCourse | null> {
    try {
      const course = await whop.courses.retrieve(courseId);
      return course as unknown as WhopCourse;
    } catch (error) {
      console.error("Failed to get course:", error);
      return null;
    }
  }

  async getCourseWithChapters(courseId: string): Promise<WhopCourseWithChapters | null> {
    try {
      const course = await this.getCourse(courseId);
      if (!course) return null;

      const chapters = await this.listChapters(courseId);
      const chaptersWithLessons: WhopChapterWithLessons[] = [];

      for (const chapter of chapters) {
        const lessons = await this.listLessons(chapter.id);
        chaptersWithLessons.push({
          ...chapter,
          lessons,
        });
      }

      return {
        ...course,
        chapters: chaptersWithLessons,
      };
    } catch (error) {
      console.error("Failed to get course with chapters:", error);
      return null;
    }
  }

  async createCourse(experienceId: string, data: CreateCourseInput): Promise<WhopCourse | null> {
    const createParams: any = {
      experience_id: experienceId,
      title: data.title.substring(0, 100),
    };

    if (data.tagline) {
      createParams.tagline = data.tagline.substring(0, 200);
    }
    if (data.visibility) createParams.visibility = data.visibility;
    if (data.certificate_after_completion_enabled !== undefined) {
      createParams.certificate_after_completion_enabled = data.certificate_after_completion_enabled;
    }
    if (data.require_completing_lessons_in_order !== undefined) {
      createParams.require_completing_lessons_in_order = data.require_completing_lessons_in_order;
    }

    try {
      console.log("[WhopCourses] Creating course with params:", JSON.stringify(createParams, null, 2));
      const course = await whop.courses.create(createParams);
      console.log("[WhopCourses] Course created successfully:", course.id);
      return course as unknown as WhopCourse;
    } catch (error: any) {
      console.error("[WhopCourses] Failed to create course. Params:", JSON.stringify(createParams, null, 2));
      console.error("[WhopCourses] Error:", error?.message || error);
      if (error?.error) {
        console.error("[WhopCourses] Error details:", JSON.stringify(error.error, null, 2));
      }

      if (error?.status === 500) {
        console.log("[WhopCourses] Retrying with minimal params...");
        try {
          const minimalParams = {
            experience_id: experienceId,
            title: data.title.substring(0, 50),
          };
          console.log("[WhopCourses] Minimal params:", JSON.stringify(minimalParams, null, 2));
          const course = await whop.courses.create(minimalParams);
          console.log("[WhopCourses] Course created with minimal params:", course.id);
          return course as unknown as WhopCourse;
        } catch (retryError: any) {
          console.error("[WhopCourses] Retry also failed:", retryError?.message || retryError);
          throw retryError;
        }
      }
      throw error;
    }
  }

  async updateCourse(courseId: string, data: UpdateCourseInput): Promise<WhopCourse | null> {
    try {
      const updateParams: any = {};

      if (data.title !== undefined) updateParams.title = data.title;
      if (data.description !== undefined) updateParams.description = data.description;
      if (data.tagline !== undefined) updateParams.tagline = data.tagline;
      if (data.visibility !== undefined) updateParams.visibility = data.visibility;
      if (data.language !== undefined) updateParams.language = data.language;
      if (data.certificate_after_completion_enabled !== undefined) {
        updateParams.certificate_after_completion_enabled = data.certificate_after_completion_enabled;
      }
      if (data.require_completing_lessons_in_order !== undefined) {
        updateParams.require_completing_lessons_in_order = data.require_completing_lessons_in_order;
      }

      const course = await whop.courses.update(courseId, updateParams);
      return course as unknown as WhopCourse;
    } catch (error) {
      console.error("Failed to update course:", error);
      return null;
    }
  }

  async deleteCourse(courseId: string): Promise<boolean> {
    try {
      await whop.courses.delete(courseId);
      return true;
    } catch (error) {
      console.error("Failed to delete course:", error);
      return false;
    }
  }

  async listChapters(courseId: string): Promise<WhopChapter[]> {
    try {
      const chapters: WhopChapter[] = [];
      for await (const chapter of whop.courseChapters.list({ course_id: courseId })) {
        chapters.push(chapter as unknown as WhopChapter);
      }
      return chapters.sort((a, b) => a.order - b.order);
    } catch (error) {
      console.error("Failed to list chapters:", error);
      return [];
    }
  }

  async getChapter(chapterId: string): Promise<WhopChapter | null> {
    try {
      const chapter = await whop.courseChapters.retrieve(chapterId);
      return chapter as unknown as WhopChapter;
    } catch (error) {
      console.error("Failed to get chapter:", error);
      return null;
    }
  }

  async createChapter(courseId: string, data: CreateChapterInput): Promise<WhopChapter | null> {
    try {
      const chapter = await whop.courseChapters.create({
        course_id: courseId,
        title: data.title,
      });
      return chapter as unknown as WhopChapter;
    } catch (error) {
      console.error("Failed to create chapter:", error);
      return null;
    }
  }

  async updateChapter(chapterId: string, data: UpdateChapterInput): Promise<WhopChapter | null> {
    try {
      const updateParams: any = {};
      if (data.title !== undefined) updateParams.title = data.title;

      const chapter = await whop.courseChapters.update(chapterId, updateParams);
      return chapter as unknown as WhopChapter;
    } catch (error) {
      console.error("Failed to update chapter:", error);
      return null;
    }
  }

  async deleteChapter(chapterId: string): Promise<boolean> {
    try {
      await whop.courseChapters.delete(chapterId);
      return true;
    } catch (error) {
      console.error("Failed to delete chapter:", error);
      return false;
    }
  }

  async listLessons(chapterId?: string, courseId?: string): Promise<WhopLesson[]> {
    try {
      const lessons: WhopLesson[] = [];
      const params: any = { first: 100 };
      if (chapterId) params.chapter_id = chapterId;
      if (courseId) params.course_id = courseId;

      for await (const lesson of whop.courseLessons.list(params)) {
        lessons.push(lesson as unknown as WhopLesson);
      }
      return lessons.sort((a, b) => a.order - b.order);
    } catch (error) {
      console.error("Failed to list lessons:", error);
      return [];
    }
  }

  async getLesson(lessonId: string): Promise<WhopLesson | null> {
    try {
      const lesson = await whop.courseLessons.retrieve(lessonId);
      return lesson as unknown as WhopLesson;
    } catch (error) {
      console.error("Failed to get lesson:", error);
      return null;
    }
  }

  async createLesson(chapterId: string, data: CreateLessonInput): Promise<WhopLesson | null> {
    try {
      const createParams: any = {
        chapter_id: chapterId,
        lesson_type: data.lesson_type,
      };

      if (data.title) createParams.title = data.title;
      if (data.content) createParams.content = data.content;
      if (data.days_from_course_start_until_unlock !== undefined) {
        createParams.days_from_course_start_until_unlock = data.days_from_course_start_until_unlock;
      }
      if (data.embed_type) createParams.embed_type = data.embed_type;
      if (data.embed_id) createParams.embed_id = data.embed_id;

      const lesson = await whop.courseLessons.create(createParams);
      return lesson as unknown as WhopLesson;
    } catch (error) {
      console.error("Failed to create lesson:", error);
      return null;
    }
  }

  async updateLesson(lessonId: string, data: UpdateLessonInput): Promise<WhopLesson | null> {
    try {
      const updateParams: any = {};

      if (data.title !== undefined) updateParams.title = data.title;
      if (data.content !== undefined) updateParams.content = data.content;
      if (data.lesson_type !== undefined) updateParams.lesson_type = data.lesson_type;
      if (data.visibility !== undefined) updateParams.visibility = data.visibility;
      if (data.days_from_course_start_until_unlock !== undefined) {
        updateParams.days_from_course_start_until_unlock = data.days_from_course_start_until_unlock;
      }
      if (data.embed_type !== undefined) updateParams.embed_type = data.embed_type;
      if (data.embed_id !== undefined) updateParams.embed_id = data.embed_id;

      const lesson = await whop.courseLessons.update(lessonId, updateParams);
      return lesson as unknown as WhopLesson;
    } catch (error) {
      console.error("Failed to update lesson:", error);
      return null;
    }
  }

  async deleteLesson(lessonId: string): Promise<boolean> {
    try {
      await whop.courseLessons.delete(lessonId);
      return true;
    } catch (error) {
      console.error("Failed to delete lesson:", error);
      return false;
    }
  }

  async markLessonComplete(lessonId: string): Promise<boolean> {
    try {
      await whop.courseLessons.markAsCompleted(lessonId);
      return true;
    } catch (error) {
      console.error("Failed to mark lesson complete:", error);
      return false;
    }
  }

  async startLesson(lessonId: string): Promise<boolean> {
    try {
      await whop.courseLessons.start(lessonId);
      return true;
    } catch (error) {
      console.error("Failed to start lesson:", error);
      return false;
    }
  }

  async listStudents(courseId: string, keyword?: string): Promise<WhopStudent[]> {
    try {
      const students: WhopStudent[] = [];
      const params: any = { course_id: courseId, first: 100 };
      if (keyword) params.keyword = keyword;

      for await (const student of whop.courseStudents.list(params)) {
        students.push(student as unknown as WhopStudent);
      }
      return students;
    } catch (error) {
      console.error("Failed to list students:", error);
      return [];
    }
  }

  async getStudent(studentId: string): Promise<WhopStudentFull | null> {
    try {
      const student = await whop.courseStudents.retrieve(studentId);
      return student as unknown as WhopStudentFull;
    } catch (error) {
      console.error("Failed to get student:", error);
      return null;
    }
  }

  async listInteractions(options: {
    courseId?: string;
    userId?: string;
    lessonId?: string;
    completed?: boolean;
  } = {}): Promise<WhopInteraction[]> {
    try {
      const interactions: WhopInteraction[] = [];
      const params: any = { first: 100 };
      if (options.courseId) params.course_id = options.courseId;
      if (options.userId) params.user_id = options.userId;
      if (options.lessonId) params.lesson_id = options.lessonId;
      if (options.completed !== undefined) params.completed = options.completed;

      for await (const interaction of whop.courseLessonInteractions.list(params)) {
        interactions.push(interaction as unknown as WhopInteraction);
      }
      return interactions;
    } catch (error) {
      console.error("Failed to list interactions:", error);
      return [];
    }
  }

  async getInteraction(interactionId: string): Promise<WhopInteractionFull | null> {
    try {
      const interaction = await whop.courseLessonInteractions.retrieve(interactionId);
      return interaction as unknown as WhopInteractionFull;
    } catch (error) {
      console.error("Failed to get interaction:", error);
      return null;
    }
  }

  async getUserCourseProgress(courseId: string, userId: string): Promise<{
    completedLessons: string[];
    totalLessons: number;
    completionRate: number;
  }> {
    try {
      const interactions = await this.listInteractions({ courseId, userId, completed: true });
      const completedLessons = interactions.map(i => i.lesson.id);

      const lessons = await this.listLessons(undefined, courseId);
      const totalLessons = lessons.length;

      return {
        completedLessons,
        totalLessons,
        completionRate: totalLessons > 0 ? (completedLessons.length / totalLessons) * 100 : 0,
      };
    } catch (error) {
      console.error("Failed to get user course progress:", error);
      return { completedLessons: [], totalLessons: 0, completionRate: 0 };
    }
  }

  async getCourseListItems(experienceId?: string): Promise<CourseListItem[]> {
    try {
      const courses = await this.listCourses(experienceId);
      const items: CourseListItem[] = [];

      for (const course of courses) {
        const chapters = await this.listChapters(course.id);
        let lessonCount = 0;
        for (const chapter of chapters) {
          const lessons = await this.listLessons(chapter.id);
          lessonCount += lessons.length;
        }

        const students = await this.listStudents(course.id);
        const avgCompletionRate = students.length > 0
          ? students.reduce((acc, s) => acc + s.completion_rate, 0) / students.length
          : 0;

        items.push({
          id: course.id,
          title: course.title,
          description: course.description,
          thumbnail: course.thumbnail?.url || null,
          visibility: course.visibility,
          chapterCount: chapters.length,
          lessonCount,
          studentCount: students.length,
          completionRate: avgCompletionRate,
          created_at: course.created_at,
        });
      }

      return items;
    } catch (error) {
      console.error("Failed to get course list items:", error);
      return [];
    }
  }

  async createFullCourse(
    experienceId: string,
    courseData: {
      title: string;
      description?: string;
      chapters: {
        title: string;
        lessons: {
          title: string;
          content: string;
          lesson_type?: "text" | "video" | "pdf" | "multi" | "quiz" | "knowledge_check";
        }[];
      }[];
    }
  ): Promise<WhopCourseWithChapters | null> {
    try {
      const course = await this.createCourse(experienceId, {
        title: courseData.title,
        tagline: courseData.description,
        visibility: "hidden",
      });

      if (!course) {
        throw new Error("Failed to create course");
      }

      const chaptersWithLessons: WhopChapterWithLessons[] = [];

      for (const chapterData of courseData.chapters) {
        const chapter = await this.createChapter(course.id, {
          title: chapterData.title,
        });

        if (!chapter) {
          console.error(`Failed to create chapter: ${chapterData.title}`);
          continue;
        }

        const lessons: WhopLesson[] = [];
        for (const lessonData of chapterData.lessons) {
          const lesson = await this.createLesson(chapter.id, {
            lesson_type: lessonData.lesson_type || "text",
            title: lessonData.title,
            content: lessonData.content,
          });

          if (lesson) {
            lessons.push(lesson);
          }
        }

        chaptersWithLessons.push({
          ...chapter,
          lessons,
        });
      }

      return {
        ...course,
        chapters: chaptersWithLessons,
      };
    } catch (error) {
      console.error("Failed to create full course:", error);
      return null;
    }
  }
}

export const whopCourses = new WhopCoursesService();

